mapper.select:
 		select 
			L.lt_id,U.nickname,U.profile,U.level,L.lt_content,L.pic,L.lt_creatime
		from luntan L INNER JOIN user U ON L.user_id=U.user_id 

			L.lt_type=#{lt_type} and L.area=#{area}
			and L.type=#{type}

		order by L.lt_creatime desc
		limit #{start},#{end}

 		select count(0)
		from luntan L INNER JOIN user U ON L.user_id=U.user_id 

			L.lt_type=#{lt_type} and L.area=#{area}
			and L.type=#{type}
